import { useState } from 'react';
import './App.css';

function App() {

  const [input, setInput] = useState("");
  const [word, setWord] = useState("");

  const handleButtonClick = () => {
    setWord(input);
    // API call
  }

  return (
    <div className="App">
      <header className="App-header">
        <h2>Thesaurus</h2>
        <input id="searchInput" name="searchInput" value={input} onChange={(e) => setInput(e.target.value)}></input>
        <button onClick={handleButtonClick}>Search</button>
        { word !== "" && 
          <h3>Synonyms for \"{word}:</h3>
        }
      </header>
    </div>
  );
}

export default App;
